import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaClock, FaUsers, FaCalendar, FaCalendarCheck, FaSignOutAlt } from 'react-icons/fa';
import './App.css';

const AdminDashboard = () => {
  const [attendanceData, setAttendanceData] = useState([]);
  const [travelLogs, setTravelLogs] = useState([]);
  const [leaveRequests, setLeaveRequests] = useState([]);
  const [users, setUsers] = useState([]);
  const [newUserName, setNewUserName] = useState('');
  const [newUserEmail, setNewUserEmail] = useState('');
  const [newLeaveUser, setNewLeaveUser] = useState('');
  const [newLeaveType, setNewLeaveType] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    setAttendanceData([]);
    setTravelLogs([]);
    setLeaveRequests([]);
    setUsers([]);
  }, []);

  const handleApproveLeave = (id) => {
    setLeaveRequests((prevRequests) =>
      prevRequests.map((request) =>
        request.id === id ? { ...request, status: 'Approved' } : request
      )
    );
  };

  const handleDenyLeave = (id) => {
    setLeaveRequests((prevRequests) =>
      prevRequests.map((request) =>
        request.id === id ? { ...request, status: 'Denied' } : request
      )
    );
  };

  const handleLogout = () => {
    navigate('/');
  };

  const handleNewUserNameChange = (e) => {
    setNewUserName(e.target.value);
  };

  const handleNewUserEmailChange = (e) => {
    setNewUserEmail(e.target.value);
  };

  const handleAddUser = () => {
    if (newUserName.trim() === '' || newUserEmail.trim() === '') {
      alert('Please enter both name and email.');
      return;
    }
    const newUser = {
      id: Date.now(),
      name: newUserName,
      email: newUserEmail,
    };
    setUsers((prevUsers) => [...prevUsers, newUser]);
    setNewUserName('');
    setNewUserEmail('');
  };

  const handleNewLeaveUserChange = (e) => {
    setNewLeaveUser(e.target.value);
  };

  const handleNewLeaveTypeChange = (e) => {
    setNewLeaveType(e.target.value);
  };

  const handleSubmitLeaveRequest = () => {
    if (newLeaveUser.trim() === '' || newLeaveType.trim() === '') {
      alert('Please enter both user and leave type.');
      return;
    }
    const newRequest = {
      id: Date.now(),
      user: newLeaveUser,
      type: newLeaveType,
      status: 'Pending',
    };
    setLeaveRequests((prevRequests) => [...prevRequests, newRequest]);
    setNewLeaveUser('');
    setNewLeaveType('');
  };

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <h2>Admin Dashboard</h2>
        <div className="logout-btn">
          <button className="btn btn-danger" onClick={handleLogout}>
            <FaSignOutAlt /> Logout
          </button>
        </div>
      </div>

      <div className="dashboard-content">
        {/* Attendance Logs */}
        <div className="section attendance">
          <h3>Attendance Logs</h3>
          <table className="custom-table">
            <thead>
              <tr>
                <th>Name</th>
                <th>Status</th>
                <th>Clock In</th>
                <th>Clock Out</th>
              </tr>
            </thead>
            <tbody>
              {attendanceData.length === 0 ? (
                <tr><td colSpan="4">No attendance data available</td></tr>
              ) : (
                attendanceData.map((entry) => (
                  <tr key={entry.id}>
                    <td>{entry.name}</td>
                    <td>{entry.status}</td>
                    <td>{entry.clockIn}</td>
                    <td>{entry.clockOut}</td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>

        {/* Travel Logs */}
        <div className="section travel-logs">
          <h3>Travel Logs</h3>
          <table className="custom-table">
            <thead>
              <tr>
                <th>User</th>
                <th>Distance (km)</th>
                <th>Fuel Cost</th>
                <th>Toll Cost</th>
              </tr>
            </thead>
            <tbody>
              {travelLogs.length === 0 ? (
                <tr><td colSpan="4">No travel logs available</td></tr>
              ) : (
                travelLogs.map((log) => (
                  <tr key={log.id}>
                    <td>{log.user}</td>
                    <td>{log.distance}</td>
                    <td>{log.fuelCost}</td>
                    <td>{log.tollCost}</td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>

        {/* Leave Requests */}
        <div className="section leave-requests">
          <h3>Leave Requests</h3>

          {/* Leave Request Form */}
          <div className="add-leave-request-form">
            <input
              type="text"
              placeholder="User Name"
              value={newLeaveUser}
              onChange={handleNewLeaveUserChange}
              className="form-input"
            />
            <input
              type="text"
              placeholder="Leave Type"
              value={newLeaveType}
              onChange={handleNewLeaveTypeChange}
              className="form-input"
            />
            <button onClick={handleSubmitLeaveRequest} className="btn btn-primary">
              Submit Leave Request
            </button>
          </div>

          <table className="custom-table">
            <thead>
              <tr>
                <th>User</th>
                <th>Leave Type</th>
                <th>Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {leaveRequests.length === 0 ? (
                <tr><td colSpan="4">No leave requests available</td></tr>
              ) : (
                leaveRequests.map((request) => (
                  <tr key={request.id}>
                    <td>{request.user}</td>
                    <td>{request.type}</td>
                    <td>{request.status}</td>
                    <td>
                      {request.status === 'Pending' && (
                        <>
                          <button
                            onClick={() => handleApproveLeave(request.id)}
                            className="btn btn-success"
                          >
                            Approve
                          </button>
                          <button
                            onClick={() => handleDenyLeave(request.id)}
                            className="btn btn-danger"
                          >
                            Deny
                          </button>
                        </>
                      )}
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>

        {/* Manage Users */}
        <div className="section manage-users">
          <h3>Manage Users</h3>
          <div className="add-user-form">
            <input
              type="text"
              placeholder="Name"
              value={newUserName}
              onChange={handleNewUserNameChange}
              className="form-input"
            />
            <input
              type="email"
              placeholder="Email"
              value={newUserEmail}
              onChange={handleNewUserEmailChange}
              className="form-input"
            />
            <button onClick={handleAddUser} className="btn btn-primary">
              Add User
            </button>
          </div>
          <table className="custom-table">
            <thead>
              <tr>
                <th>Name</th>
                <th>Email</th>
              </tr>
            </thead>
            <tbody>
              {users.length === 0 ? (
                <tr><td colSpan="2">No users added</td></tr>
              ) : (
                users.map((user) => (
                  <tr key={user.id}>
                    <td>{user.name}</td>
                    <td>{user.email}</td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>

        {/* Navigation Links */}
        <div className="navigation-links">
          <h3>Navigation</h3>
          <ul>
            <li><a href="/manage-users"><FaUsers /> Manage Users</a></li>
            <li><a href="/attendance-logs"><FaCalendarCheck /> Attendance Logs</a></li>
            <li><a href="/travel-logs"><FaCalendar /> Travel Logs</a></li>
            <li><a href="/leave-requests"><FaCalendarCheck /> Leave Requests</a></li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
