import React, { useState, useEffect } from 'react';
import apiService from '../api/apiService';
import '../App.css';

const ManageUsers = () => {
  // State for the form
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('employee');
  const [position, setPosition] = useState('');
  const [managerId, setManagerId] = useState('');
  
  // State for UI feedback
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [loading, setLoading] = useState(false);
  
  // State for data
  const [users, setUsers] = useState([]);
  const [managers, setManagers] = useState([]);
  const [loadingUsers, setLoadingUsers] = useState(true);

  // Fetch managers for the dropdown
  useEffect(() => {
    const fetchManagers = async () => {
      try {
        const response = await apiService.get('/admin/managers');
        setManagers(response.data);
      } catch (err) {
        console.error("Failed to fetch managers:", err);
        setError("Could not load the list of managers.");
      }
    };
    
    fetchUsers(); // You can implement this when an endpoint is available
    fetchManagers();
  }, []);

  const fetchUsers = async () => {
    setLoadingUsers(true);
    // TODO: Implement user fetching when an endpoint to list all users is created.
    setUsers([]);
    setLoadingUsers(false);
  };

  const handleAddUser = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setSuccess('');

    try {
      // Use the new, secured endpoint for creating users
      const response = await apiService.post('/admin/users', {
        name,
        email,
        password,
        password_confirmation: password,
        role,
        position,
        manager_id: managerId || null, // Send null if no manager is selected
      });
      setSuccess(`User "${response.data.user.name}" created successfully!`);

      // Reset form fields
      setName('');
      setEmail('');
      setPassword('');
      setRole('employee');
      setPosition('');
      setManagerId('');
      // fetchUsers(); // Re-fetch users list after adding a new one
    } catch (err) {
      const errorMessage = err.response?.data?.message || 'Failed to create user.';
      setError(errorMessage);
      console.error('Add user failed:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <div className="section">
        <h3>Add New User</h3>
        {error && <p className="error">{error}</p>}
        {success && <p style={{ color: 'green', marginBottom: '1rem' }}>{success}</p>}
        <form onSubmit={handleAddUser} className="leave-request-form">
          <div>
            <label>Full Name</label>
            <input type="text" placeholder="John Doe" value={name} onChange={(e) => setName(e.target.value)} required />
          </div>
          <div>
            <label>Email</label>
            <input type="email" placeholder="user@example.com" value={email} onChange={(e) => setEmail(e.target.value)} required />
          </div>
          <div>
            <label>Password</label>
            <input type="password" placeholder="********" value={password} onChange={(e) => setPassword(e.target.value)} required />
          </div>
          <div>
            <label>Position</label>
            <input type="text" placeholder="e.g., Software Developer" value={position} onChange={(e) => setPosition(e.target.value)} required />
          </div>
          <div>
            <label>Role</label>
            <select value={role} onChange={(e) => setRole(e.target.value)}>
              <option value="employee">Employee</option>
              <option value="manager">Manager</option>
              <option value="hr">HR</option>
            </select>
          </div>
          <div>
            <label>Reports To (Manager)</label>
            <select value={managerId} onChange={(e) => setManagerId(e.target.value)}>
              <option value="">-- No Manager --</option>
              {managers.map(manager => (
                <option key={manager.employee.id} value={manager.employee.id}>
                  {manager.name} ({manager.role})
                </option>
              ))}
            </select>
          </div>
          <button type="submit" className="btn btn-primary" disabled={loading}>
            {loading ? 'Creating User...' : 'Create User'}
          </button>
        </form>
      </div>

      <div className="section">
        <h3>Existing Users</h3>
        {loadingUsers ? <p>Loading users...</p> : (
          <table className="table table-bordered">
            <thead><tr><th>Name</th><th>Email</th><th>Role</th></tr></thead>
            <tbody>
              {users.length > 0 ? users.map(user => (
                <tr key={user.id}>
                  <td>{user.name}</td>
                  <td>{user.email}</td>
                  <td>{user.role}</td>
                </tr>
              )) : <tr><td colSpan="3">No users found. (Listing not implemented)</td></tr>}
            </tbody>
          </table>
        )}
      </div>
    </>
  );
};

export default ManageUsers;
