import React, { useState, useEffect } from 'react';
import apiService from '../api/apiService';

const AttendanceLogs = () => {
  const [attendanceData, setAttendanceData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchAttendance = async () => {
      setLoading(true);
      try {
        const response = await apiService.get('/attendance/history');
        setAttendanceData(response.data.data);
      } catch (error) {
        console.error("Failed to fetch attendance logs:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchAttendance();
  }, []);

  // This component now returns only its specific content,
  // allowing PageLayout to handle the title and container.
  return (
    <>
      {loading ? <p>Loading logs...</p> : (
        <table className="table table-bordered">
          <thead>
            <tr>
              <th>Date</th>
              <th>Status</th>
              <th>Clock In</th>
              <th>Clock Out</th>
              <th>Worked Hours</th>
            </tr>
          </thead>
          <tbody>
            {attendanceData.length === 0 ? (
              <tr><td colSpan="5">No attendance data available</td></tr>
            ) : (
              attendanceData.map((entry) => (
                <tr key={entry.id}>
                  <td>{new Date(entry.clock_in_time).toLocaleDateString()}</td>
                  <td>{entry.status}</td>
                  <td>{new Date(entry.clock_in_time).toLocaleTimeString()}</td>
                  <td>{entry.clock_out_time ? new Date(entry.clock_out_time).toLocaleTimeString() : 'N/A'}</td>
                  <td>{entry.worked_hours || 'N/A'}</td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      )}
    </>
  );
};

export default AttendanceLogs;
