import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaUsers, FaCalendarCheck, FaSignOutAlt } from 'react-icons/fa';
import apiService from './api/apiService';
import { useAuth } from './AuthContext';
import './App.css';

const AdminDashboard = () => {
  const [leaveRequests, setLeaveRequests] = useState([]);
  const [suspiciousActivities, setSuspiciousActivities] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();
  const { logout } = useAuth();

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        const [leavesResponse, suspiciousResponse] = await Promise.all([
          apiService.get('/manager/leave/pending-requests'),
          apiService.get('/manager/suspicious-activity')
        ]);
        setLeaveRequests(leavesResponse.data);
        setSuspiciousActivities(suspiciousResponse.data);
      } catch (error) {
        console.error("Failed to fetch admin data:", error);
        // Optionally, handle errors, e.g., show a notification
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleApproveLeave = async (id) => {
    try {
      await apiService.post(`/manager/leave/requests/${id}/approve`);
      setLeaveRequests((prev) => prev.filter((r) => r.id !== id));
      alert('Leave request approved.');
    } catch (error) {
      console.error('Failed to approve leave request:', error);
      alert('Error: Could not approve leave request.');
    }
  };

  const handleDenyLeave = async (id) => {
    const reason = prompt("Please provide a reason for rejection:");
    if (!reason) return; // User cancelled the prompt

    try {
      await apiService.post(`/manager/leave/requests/${id}/reject`, { rejection_reason: reason });
      setLeaveRequests((prev) => prev.filter((r) => r.id !== id));
      alert('Leave request rejected.');
    } catch (error) {
      console.error('Failed to reject leave request:', error);
      alert('Error: Could not reject leave request.');
    }
  };

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <h2>Admin Dashboard</h2>
        <div className="logout-btn">
          <button className="btn btn-danger" onClick={handleLogout}>
            <FaSignOutAlt /> Logout
          </button>
        </div>
      </div>

      {loading ? (
        <p>Loading dashboard data...</p>
      ) : (
        <div className="dashboard-content">
          <div className="section leave-requests">
            <h3>Pending Leave Requests</h3>
            <table className="table table-bordered">
              <thead>
                <tr>
                  <th>Employee</th>
                  <th>Leave Type</th>
                  <th>Dates</th>
                  <th>Reason</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {leaveRequests.length === 0 ? (
                  <tr><td colSpan="5">No pending leave requests.</td></tr>
                ) : (
                  leaveRequests.map((request) => (
                    <tr key={request.id}>
                      <td>{request.employee.user.name}</td>
                      <td>{request.leave_type}</td>
                      <td>{request.start_date} to {request.end_date}</td>
                      <td>{request.reason}</td>
                      <td>
                        <button onClick={() => handleApproveLeave(request.id)} className="btn btn-success btn-sm me-2">Approve</button>
                        <button onClick={() => handleDenyLeave(request.id)} className="btn btn-danger btn-sm">Deny</button>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>

          <div className="section suspicious-activity">
            <h3>Suspicious Activity</h3>
            <table className="table table-bordered">
              <thead>
                <tr>
                  <th>Employee</th>
                  <th>Clock-In Time</th>
                  <th>Location</th>
                </tr>
              </thead>
              <tbody>
                {suspiciousActivities.length === 0 ? (
                  <tr><td colSpan="3">No suspicious activities detected.</td></tr>
                ) : (
                  suspiciousActivities.map((activity) => (
                    <tr key={activity.id}>
                      <td>{activity.employee.user.name}</td>
                      <td>{new Date(activity.clock_in_time).toLocaleString()}</td>
                      <td>Lat: {activity.clock_in_latitude}, Lon: {activity.clock_in_longitude}</td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>

          <div className="navigation-links">
            <h3>Management Links</h3>
            <ul>
              <li><a href="/manage-users"><FaUsers /> Manage Users</a></li>
              <li><a href="/attendance-logs"><FaCalendarCheck /> All Attendance Logs</a></li>
            </ul>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminDashboard;
