import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import apiService from '../api/apiService';
import '../App.css';

const LeaveHistoryPage = () => {
  const [leaveRequests, setLeaveRequests] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchLeaveHistory = async () => {
      setLoading(true);
      try {
        const response = await apiService.get('/leave/history');
        setLeaveRequests(response.data.data);
      } catch (error) {
        console.error("Failed to fetch leave history:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchLeaveHistory();
  }, []);

  return (
    <div className="dashboard">
      <h2 className="dashboard-header">My Leave Request History</h2>
      <div className="section">
        {loading ? (
          <p>Loading history...</p>
        ) : (
          <table className="table table-bordered">
            <thead>
              <tr>
                <th>Dates</th>
                <th>Type</th>
                <th>Reason</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {leaveRequests.length > 0 ? (
                leaveRequests.map((log) => (
                  <tr key={log.id}>
                    <td>{new Date(log.start_date).toLocaleDateString()} - {new Date(log.end_date).toLocaleDateString()}</td>
                    <td>{log.leave_type}</td>
                    <td>{log.reason}</td>
                    <td>
                      <span className={`badge ${log.status === 'approved' ? 'bg-success' : log.status === 'rejected' ? 'bg-danger' : 'bg-warning'}`}>
                        {log.status}
                      </span>
                    </td>
                  </tr>
                ))
              ) : (
                <tr><td colSpan="4">No leave history found.</td></tr>
              )}
            </tbody>
          </table>
        )}
        <div style={{ marginTop: '1.5rem' }}>
          <Link to="/dashboard" style={{ textDecoration: 'none', color: '#00cec9' }}>Back to Dashboard</Link>
        </div>
      </div>
    </div>
  );
};

export default LeaveHistoryPage;
