import React, { useState, useEffect } from 'react';
import './App.css'; // Ensure you have styles applied from the main CSS

const LeaveRequests = () => {
  const [leaveRequests, setLeaveRequests] = useState([]);

  // Simulate fetching data
  useEffect(() => {
    // You can fetch real data here later
    setLeaveRequests([
      { id: 1, user: 'Alice', type: 'Annual', status: 'Pending' },
      { id: 2, user: 'Bob', type: 'Sick', status: 'Approved' },
    ]);
  }, []);

  const handleApproveLeave = (id) => {
    setLeaveRequests((prev) =>
      prev.map((req) =>
        req.id === id ? { ...req, status: 'Approved' } : req
      )
    );
  };

  const handleDenyLeave = (id) => {
    setLeaveRequests((prev) =>
      prev.map((req) =>
        req.id === id ? { ...req, status: 'Denied' } : req
      )
    );
  };

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <h2>Leave Requests</h2>
      </div>

      <div className="dashboard-content">
        <div className="section leave-requests">
          <table className="custom-table">
            <thead>
              <tr>
                <th>User</th>
                <th>Leave Type</th>
                <th>Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {leaveRequests.length === 0 ? (
                <tr><td colSpan="4">No leave requests available</td></tr>
              ) : (
                leaveRequests.map((request) => (
                  <tr key={request.id}>
                    <td>{request.user}</td>
                    <td>{request.type}</td>
                    <td>{request.status}</td>
                    <td>
                      {request.status === 'Pending' && (
                        <>
                          <button
                            onClick={() => handleApproveLeave(request.id)}
                            className="btn btn-success"
                          >
                            Approve
                          </button>
                          <button
                            onClick={() => handleDenyLeave(request.id)}
                            className="btn btn-danger"
                          >
                            Deny
                          </button>
                        </>
                      )}
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default LeaveRequests;
