import React, { useState, useEffect } from 'react';
import apiService from '../api/apiService';
import '../App.css';

const ClockInOutGPS = () => {
  const [todaysAttendance, setTodaysAttendance] = useState(null);
  const [isClockedIn, setIsClockedIn] = useState(false);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);

  const fetchTodaysAttendance = async () => {
    try {
      const { data } = await apiService.get('/attendance/today');
      setTodaysAttendance(data);
      setIsClockedIn(data && !data.clock_out_time);
    } catch (err) {
      console.error("Failed to fetch today's attendance", err);
      setError("Could not retrieve today's attendance status.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchTodaysAttendance();
  }, []);

  const handleClockInOut = async () => {
    setLoading(true);
    setError(null);
    try {
      const position = await new Promise((resolve, reject) => {
        navigator.geolocation.getCurrentPosition(resolve, reject);
      });
      const { latitude, longitude } = position.coords;

      const endpoint = isClockedIn ? '/attendance/clock-out' : '/attendance/clock-in';
      const response = await apiService.post(endpoint, { latitude, longitude });

      setTodaysAttendance(response.data.attendance);
      setIsClockedIn(!isClockedIn);
      alert(`Successfully clocked ${isClockedIn ? 'out' : 'in'}!`);

    } catch (err) {
      const errorMessage = err.response?.data?.message || `Failed to clock ${isClockedIn ? 'out' : 'in'}.`;
      setError(errorMessage);
      console.error('Clock-in/out failed:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="dashboard">
      <h2 className="dashboard-header">GPS Clock In / Out</h2>
      <div className="section clock-in-out">
        {loading ? (
          <p>Loading status...</p>
        ) : (
          <>
            <div className="clock-status">
              <p>
                <strong>Current Status:</strong> {isClockedIn ? 
                `Clocked In at ${new Date(todaysAttendance.clock_in_time).toLocaleTimeString()}` : 
                'Clocked Out'}
              </p>
            </div>
            {error && <div className="error">{error}</div>}
            <div className="clock-buttons">
              <button onClick={handleClockInOut} className={`clock-btn ${isClockedIn ? 'out' : 'in'}`} disabled={loading}>
                {isClockedIn ? 'Clock Out' : 'Clock In'}
              </button>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default ClockInOutGPS;
