import React, { useState } from 'react';
import apiService from '../api/apiService';

const ManageUsers = () => {
  const [users, setUsers] = useState([]); // In a real app, you'd fetch existing users.
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('employee');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [loading, setLoading] = useState(false);

  const handleAddUser = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setSuccess('');

    // TODO: The backend needs a corresponding endpoint: POST /api/manager/users
    // This endpoint should be protected and only accessible by admins.
    try {
      const response = await apiService.post('/register', { // Using register endpoint for now
        name,
        email,
        password,
        password_confirmation: password,
        role,
      });
      setSuccess(`User "${response.data.user.name}" created successfully!`);
      // Clear form
      setName('');
      setEmail('');
      setPassword('');
      setRole('employee');
    } catch (err) {
      const errorMessage = err.response?.data?.message || 'Failed to create user.';
      setError(errorMessage);
      console.error('Add user failed:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="dashboard">
      <h2 className="dashboard-header">Manage Users</h2>
      <div className="section">
        <h3>Add New User</h3>
        {error && <p className="error">{error}</p>}
        {success && <p style={{ color: 'green' }}>{success}</p>}
        <form onSubmit={handleAddUser} className="leave-request-form">
          <div>
            <label>Full Name</label>
            <input type="text" placeholder="John Doe" value={name} onChange={(e) => setName(e.target.value)} required />
          </div>
          <div>
            <label>Email</label>
            <input type="email" placeholder="user@example.com" value={email} onChange={(e) => setEmail(e.target.value)} required />
          </div>
          <div>
            <label>Password</label>
            <input type="password" placeholder="********" value={password} onChange={(e) => setPassword(e.target.value)} required />
          </div>
          <div>
            <label>Role</label>
            <select value={role} onChange={(e) => setRole(e.target.value)}>
              <option value="employee">Employee</option>
              <option value="manager">Manager</option>
              <option value="admin">Admin</option>
            </select>
          </div>
          <button type="submit" className="btn btn-primary" disabled={loading}>
            {loading ? 'Creating User...' : 'Create User'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default ManageUsers;
