import React, { useState, useEffect } from 'react';
import apiService from '../api/apiService';
import { useAuth } from '../AuthContext'; // Not used, but added to prevent potential future errors
import '../App.css';

const ManageUsers = () => {
  const [users, setUsers] = useState([]);
  const [loadingUsers, setLoadingUsers] = useState(true);
  
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('employee');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [loading, setLoading] = useState(false);

  const fetchUsers = async () => {
    setLoadingUsers(true);
    try {
      // TODO: Backend needs an endpoint for this, e.g., GET /api/manager/users
      // For now, we will just simulate an empty list.
      // const response = await apiService.get('/manager/users');
      // setUsers(response.data);
      setUsers([]); // Set to empty array to resolve 'setUsers' not used warning
      console.log("User fetching is not implemented on backend yet.");
    } catch (err) {
      console.error("Failed to fetch users:", err);
    } finally {
      setLoadingUsers(false);
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  const handleAddUser = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setSuccess('');

    try {
      const response = await apiService.post('/register', {
        name,
        email,
        password,
        password_confirmation: password,
        role,
      });
      setSuccess(`User "${response.data.user.name}" created successfully!`);
      // Clear form
      setName('');
      setEmail('');
      setPassword('');
      setRole('employee');
      fetchUsers(); // Refresh user list
    } catch (err) {
      const errorMessage = err.response?.data?.message || 'Failed to create user.';
      setError(errorMessage);
      console.error('Add user failed:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="dashboard">
      <h2 className="dashboard-header">Manage Users</h2>
      <div className="section">
        <h3>Add New User</h3>
        {error && <p className="error">{error}</p>}
        {success && <p style={{ color: 'green' }}>{success}</p>}
        <form onSubmit={handleAddUser} className="leave-request-form">
          <div>
            <label>Full Name</label>
            <input type="text" placeholder="John Doe" value={name} onChange={(e) => setName(e.target.value)} required />
          </div>
          <div>
            <label>Email</label>
            <input type="email" placeholder="user@example.com" value={email} onChange={(e) => setEmail(e.target.value)} required />
          </div>
          <div>
            <label>Password</label>
            <input type="password" placeholder="********" value={password} onChange={(e) => setPassword(e.target.value)} required />
          </div>
          <div>
            <label>Role</label>
            <select value={role} onChange={(e) => setRole(e.target.value)}>
              <option value="employee">Employee</option>
              <option value="manager">Manager</option>
              <option value="admin">Admin</option>
            </select>
          </div>
          <button type="submit" className="btn btn-primary" disabled={loading}>
            {loading ? 'Creating User...' : 'Create User'}
          </button>
        </form>
      </div>

      <div className="section">
        <h3>Existing Users</h3>
        {loadingUsers ? <p>Loading users...</p> : (
          <table className="table table-bordered">
            <thead><tr><th>Name</th><th>Email</th><th>Role</th></tr></thead>
            <tbody>
              {users.length > 0 ? users.map(user => (
                <tr key={user.id}>
                  <td>{user.name}</td>
                  <td>{user.email}</td>
                  <td>{user.role}</td>
                </tr>
              )) : <tr><td colSpan="3">No users found.</td></tr>}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default ManageUsers;
