import React, { useState, useEffect } from 'react';
import apiService from '../api/apiService';
import '../App.css';

/**
 * A simple, reusable modal component for displaying messages.
 */
const Modal = ({ message, onClose }) => {
  if (!message) return null;

  return (
    <div className="modal-backdrop">
      <div className="modal-content">
        <p>{message}</p>
        <button onClick={onClose}>Close</button>
      </div>
    </div>
  );
};

const ClockInOutGPS = () => {
  const [todaysAttendance, setTodaysAttendance] = useState(null);
  const [isClockedIn, setIsClockedIn] = useState(false);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);
  const [modalMessage, setModalMessage] = useState('');

  /**
   * Fetches the latest attendance status for the current day from the server.
   * This function ensures the UI is always in sync with the backend data.
   */
  const fetchTodaysAttendance = async () => {
    setLoading(true);
    setError(null);
    try {
      const { data } = await apiService.get('/attendance/today');
      
      if (data && data.id && data.clock_in_time) {
        setTodaysAttendance(data);
        setIsClockedIn(!data.clock_out_time);
      } else {
        setTodaysAttendance(null);
        setIsClockedIn(false);
      }
    } catch (err) {
      console.error("Failed to fetch today's attendance:", err);
      setTodaysAttendance(null);
      setIsClockedIn(false);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchTodaysAttendance();
  }, []);

  /**
   * Handles the clock-in and clock-out logic.
   */
  const handleClockInOut = async () => {
    setLoading(true);
    setError(null);
    try {
      const position = await new Promise((resolve, reject) => {
        navigator.geolocation.getCurrentPosition(resolve, reject, { timeout: 10000 });
      });
      const { latitude, longitude } = position.coords;

      const endpoint = isClockedIn ? '/attendance/clock-out' : '/attendance/clock-in';
      await apiService.post(endpoint, { latitude, longitude });

      // Show a success message in the modal instead of an alert.
      setModalMessage(`Successfully clocked ${isClockedIn ? 'out' : 'in'}!`);

      fetchTodaysAttendance();

    } catch (err) {
      let errorMessage = `Failed to clock ${isClockedIn ? 'out' : 'in'}.`;
      if (err.code && err.code === err.PERMISSION_DENIED) {
          errorMessage = "Geolocation permission denied. Please enable location services to proceed.";
      } else if (err.response?.data?.message) {
          errorMessage = err.response.data.message;
      }
      
      setError(errorMessage);
      console.error('Clock-in/out failed:', err);
      setLoading(false); 
    }
  };
  
  /**
   * Determines the status message to display based on the current state.
   * @returns {string} The status message.
   */
  const getStatusMessage = () => {
      if (loading) {
          return "Loading status...";
      }
      if (isClockedIn && todaysAttendance) {
          return `Clocked In at ${new Date(todaysAttendance.clock_in_time).toLocaleTimeString()}`;
      }
      return "Clocked Out";
  };

  return (
    <>
      {/* Self-contained CSS for the Modal component */}
      <style>
        {`
          .modal-backdrop {
            position: fixed;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background-color: rgba(0, 0, 0, 0.6);
            display: flex;
            justify-content: center;
            align-items: center;
            z-index: 1000;
          }

          .modal-content {
            background-color: #fff;
            padding: 2rem 3rem;
            border-radius: 20px;
            box-shadow: 0 10px 25px rgba(0,0,0,0.2);
            text-align: center;
            max-width: 450px;
            width: 90%;
            border: 1px solid #ddd;
          }

          .modal-content p {
            margin-bottom: 1.5rem;
            font-size: 1.2rem;
            color: #6c3483 !important;
            font-weight: 500;
          }

          .modal-content button {
            padding: 0.8rem 2rem;
            background-color: #00cec9;
            border: none;
            border-radius: 12px;
            color: white !important;
            font-weight: bold;
            cursor: pointer;
            transition: all 0.3s ease;
          }

          .modal-content button:hover {
            background-color: #00b894;
            transform: scale(1.05);
          }
        `}
      </style>
    
      <Modal message={modalMessage} onClose={() => setModalMessage('')} />

      <div className="dashboard">
        <h2 className="dashboard-header">GPS Clock In / Out</h2>
        <div className="section clock-in-out">
          <div className="clock-status">
              <p>
                  <strong>Current Status:</strong> {getStatusMessage()}
              </p>
          </div>

          {error && <div className="error">{error}</div>}

          <div className="clock-buttons">
            <button 
              onClick={handleClockInOut} 
              className={`clock-btn-enhanced ${isClockedIn ? 'out' : 'in'}`} 
              disabled={loading || (todaysAttendance && todaysAttendance.clock_out_time)}
            >
              {loading ? 'Processing...' : (isClockedIn ? 'Clock Out' : 'Clock In')}
            </button>
          </div>

          {todaysAttendance && todaysAttendance.clock_out_time && (
              <p style={{marginTop: '1.5rem', color: '#00cec9', textAlign: 'center'}}>
                You have already clocked in and out for today.
              </p>
          )}
        </div>
      </div>
    </>
  );
};

export default ClockInOutGPS;
